import React, { useState, useEffect } from 'react';
import ApiCalendar from 'react-google-calendar-api';

const config = {
  "clientId": process.env.REACT_APP_CLIENT_ID,
  "apiKey": process.env.REACT_APP_API_KEY,
  "scope": "https://www.googleapis.com/auth/userinfo.email https://www.googleapis.com/auth/calendar.readonly https://www.googleapis.com/auth/calendar.events https://www.googleapis.com/auth/calendar",
  "discoveryDocs": [
    "https://www.googleapis.com/discovery/v1/apis/calendar/v3/rest"
  ]
};

const scriptSrcGoogle = "https://accounts.google.com/gsi/client";
const scriptSrcGapi = "https://apis.google.com/js/api.js";

const CalendarAPI2 = (props) => {

  const gapi = window.gapi;
  var tokenClient =  null;
  var onLoadCallback = null;

  useEffect(() => {

    console.log(gapi)
    handleClientLoad();

    }, []
  );

  const handleClientLoad = () => {
    const scriptGoogle = document.createElement("script");
    const scriptGapi = document.createElement("script");
    scriptGoogle.src = scriptSrcGoogle;
    scriptGoogle.async = true;
    scriptGoogle.defer = true;
    scriptGapi.src = scriptSrcGapi;
    scriptGapi.async = true;
    scriptGapi.defer = true;
    document.body.appendChild(scriptGapi);
    document.body.appendChild(scriptGoogle);
    scriptGapi.onload = () => {
      gapi.load("client", initGapiClient);
    };
    scriptGoogle.onload = async () => {
        tokenClient = await window.google.accounts.oauth2.initTokenClient({
        client_id: config.clientId,
        scope: config.scope,
        prompt: "",
        callback: () => {},
      });
    };
  }

  const initGapiClient = () => {
    gapi.client
      .init({
        apiKey: config.apiKey,
        discoveryDocs: config.discoveryDocs,
        hosted_domain: config.hosted_domain,
      })
      .then( () => {
        if (onLoadCallback) {
          onLoadCallback();
        }
      })
      .catch( (e) => {
        console.log(e);
      });
  }

  return (
      <React.Fragment>
          <a href="/">Home</a>



          <br />

      </React.Fragment>
  );
}

export default CalendarAPI2
