import { GoogleLogin } from 'react-google-login';
import { gapi } from 'gapi-script';
import React from 'react';

class Calendar extends React.Component {

  constructor() {
    super();
    this.state = {
      profileObj : "",
      firstName : "",
      lastName : "",
      gmail : ""
    };
    this.clientId = '832026479166-3i3nl2i2nngrpjer3rjoi85a18v4ufc0.apps.googleusercontent.com';
  }

  onSuccess = (res) => {
    this.state.profile = res.profileObj;
    console.log('success:', res);
  }

  onFailure = (err) => {
    console.log('failed:', err);
  }

  componentDidMount = () => {
    const initClient = () => {
      gapi.client.init({
        clientId: this.clientId,
        scope: ''
      });
    };
    gapi.load('client:auth2', initClient);
  }

  render() {
    return (
      <React.Fragment>
        <p>Sign in to download calendar data</p>
        <button>Sign in with Google</button>
        <a href="/meeting">Meeting Page</a>
        <p>This will contain an empty calendar which will be filled in when signed in</p>

      </React.Fragment>
    )
  }
}

export default Calendar
