import React, { useState, useEffect } from 'react';

const config = {
  "clientId": process.env.REACT_APP_CLIENT_ID,
  "apiKey": process.env.REACT_APP_API_KEY,
  "scope": "https://www.googleapis.com/auth/userinfo.email https://www.googleapis.com/auth/calendar.readonly https://www.googleapis.com/auth/calendar.events https://www.googleapis.com/auth/calendar",
  "discoveryDocs": [
    "https://www.googleapis.com/discovery/v1/apis/calendar/v3/rest"
  ]
};

const gapi = window.gapi

class CalendarAPITwo extends React.Component {

    constructor() {
      super();
      this.state = {

      };
    }

    handleClick = () => {
      gapi.load('client:auth2', () => {
        console.log("loaded client");

        gapi.client.init({
          apiKey: config.apiKey,
          clientId: config.clientId,
          discoveryDocs: config.discoveryDocs,
          scope: config.scope
        })


      })
    }

    render() {
      return (
        <React.Fragment>

          <a href="/meeting">Home</a>
          <a href="/CalendarAPI">CalendarAPI</a>

          <button onClick={this.handleClick}>sign in</button>

        </React.Fragment>
      )
    }
  }

  export default CalendarAPITwo
