import React, { useState, useEffect } from 'react';

import GoogleCalendar from './CalendarAPI2';
import ApiCalendar from 'react-google-calendar-api';

const CalendarAPI = () => {

    const config = {
        "clientId": "832026479166-3i3nl2i2nngrpjer3rjoi85a18v4ufc0.apps.googleusercontent.com",
        "apiKey": "AIzaSyD-rsdUh4BKKlvbkYvjJRJSVV1T-KiBXQE",
        "scope": "https://www.googleapis.com/auth/userinfo.email https://www.googleapis.com/auth/calendar.readonly https://www.googleapis.com/auth/calendar.events https://www.googleapis.com/auth/calendar",
        "discoveryDocs": [
          "https://www.googleapis.com/discovery/v1/apis/calendar/v3/rest"
        ]
    }

    const [events, setEvents] = useState(null);

    const formatEvents = (list) => {
        return list.map((item) => ({
          title: item.summary,
          start: item.start.dateTime || item.start.date,
          end: item.end.dateTime || item.end.date,
        }));
      };

    useEffect(() => {
        const script = document.createElement("script");
        script.async = true;
        script.defer = true;
        script.src = "https://apis.google.com/js/api.js";

        document.body.appendChild(script);

        script.addEventListener("load", () => {
          if (window.gapi) {
            window.gapi.load("client:auth2", initClient);
          }
        });
      }, []);

    const initClient = () => {
          fetch(
            `https://www.googleapis.com/calendar/v3/calendars/primary/events?key=${config.apiKey}&orderBy=startTime&singleEvents=true`,
            {
              headers: {
                Authorization: `Bearer ${localStorage.getItem("access_token")}`,
              },
            }
          )
            .then((res) => {
              if (res.status !== 401) {
                return res.json();
              } 
            })
            .then((data) => {
              if (data?.items) {
                console.log(data);
                setEvents(formatEvents(data.items));
              }
            });
      };

      const listUpcomingEvents = () => {
        /*
                let date = new Date();
        date.setDate(date.getDate() - 7);
        console.log("Date:", date)

        window.gapi.client.calendar.events
          .list({
            calendarId: "primary",
            timeMin: date.toISOString(),
            timeMax: (new Date()).toISOString(),
            showDeleted: true,
            singleEvents: true,
            maxResults: 100,
            orderBy: "startTime",
          })
        */

        let minDate = new Date();
        let maxDate = new Date();

        minDate.setDate(minDate.getDate() - 7);
        maxDate.setDate(maxDate.getDate() + 7);

        window.gapi.client.calendar.events
          .list({
            calendarId: "primary",
            timeMin: minDate.toISOString(),
            timeMax: maxDate.toISOString(),
            showDeleted: true,
            singleEvents: true,
            maxResults: 100,
            orderBy: "startTime",
          })
          .then(function (response) {
            var events = response.result.items.reverse();

            console.log("EVENTS:", events);

            events.forEach( (event) => {
                console.log(event.summary, event.start.dateTime.slice(0,10));
                console.log(`${event.start.dateTime.slice(12,)}`)
            })

            if (events.length > 0) {
              setEvents(formatEvents(events));
            }
          });
      };

    const apiCalendar = new ApiCalendar(config)

    const [promise, setPromise] = useState(0);

    const authCal = () => {
        apiCalendar.handleAuthClick();
        setPromise(true);
    }

    const showCalendars = () => {
        apiCalendar.listCalendars().then(({ result }) => {
            result.items.forEach((event) => {
                let info = `Summary: ${event.summary}`;
                if (event.summaryOverride) {
                    info += `, Override: ${event.summaryOverride}`;
                }
                console.log(info);
            })
        });
    }

    const showEvents = () => {
        listUpcomingEvents();
    }

    const showPromise = () => {
        console.log(promise);
    }

    const test = () => {
        console.log("promise:", promise)
        if (promise){
            return <GoogleCalendar />
        }
        else {
            return <p>Sign in first</p>
        }
    }

    const test2 = () => {
        console.log(<GoogleCalendar />)
    }

    return (
        <React.Fragment>
            <a href="/">Home</a>

            <button onClick={authCal}>Authenticate Calendar</button>

            <button onClick={showPromise}>Show promise</button>

            <button onClick={showEvents}>Show events</button>

            <button onClick={showCalendars}>Show calendars</button>

            <p>Auth promise: {promise}</p>

            <br />

            <button onClick={test}>Run new one</button>
            <p>Here: {test()}</p>

        </React.Fragment>
    );
}

export default CalendarAPI
